Full parameterization using a glue_sql templateglue_sql_mean &lt;- function(con,
                     group_cols,
                     transformation_col,
                     metric_col,
                     from_tbl) {
     # Create parameterized query with glue_sql
     query &lt;- glue::glue_sql("
     SELECT
          {`group_cols`*}
          ,AVG({`transformation_col`}) AS {`metric_col`}
     FROM {`from_tbl`}
     GROUP BY {`group_cols`*}
     ORDER BY {`group_cols`*}
     ", .con = con)
     return(dbGetQuery(con, query))
}

glue_sql_sum &lt;- function(con,
                     group_cols,
                     transformation_col,
                     metric_col,
                     from_tbl) {
     query &lt;- glue::glue_sql("
     SELECT
          {`group_cols`*}
          ,SUM({`transformation_col`}) AS {`metric_col`}
     FROM {`from_tbl`}
     GROUP BY {`group_cols`*}
     ORDER BY {`group_cols`*}
     ", .con = con)
     return(dbGetQuery(con, query))
}

glue_sql_count &lt;- function(con,
                     group_cols,
                     transformation_col,
                     metric_col,
                     from_tbl) {
     query &lt;- glue::glue_sql("
     SELECT
          {`group_cols`*}
          ,COUNT({`transformation_col`}) AS {`metric_col`}
     FROM {`from_tbl`}
     GROUP BY {`group_cols`*}
     ORDER BY {`group_cols`*}
     ", .con = con)
     return(dbGetQuery(con, query))
}





